package bloque4;

public class DoWhiles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 /* Ciclo While
		 * 
		 * do{
		 * 		Instrucciones;
		 * } while(condicion);
		 * 
		 */
		
		int n = 1;
		
		do {
			System.out.println(n);
			n++;
		} while (n<=30);

	}

}
