package bloque4;


public class For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Ciclo For
		 * 
		 * for (inicializacion; condicion; aumento o decremento){
		 * 		Intrucciones;
		 * }
		 */
		
		for (int i =0 ; i<30; i++) {
			System.out.println(i);
		}

	}

}
