package controlador;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Tipos de datos Primitivos
		 */

		// TIPOS DE DATOS ENTEROS

		byte edad;
		edad = 21;
		System.out.println("Mi edad es: " + edad);

		short entera;
		entera = 12232;
		System.out.println("El short es: " + entera);

		int entero;
		entero = 943453231;
		System.out.println("El int es: " + entero);

		int simbolo = '#';
		System.out.println("El simbolo tine un valor " + simbolo);

		long longitudMasGrande;
		longitudMasGrande = 232232802;
		System.out.println("EL long es: " + longitudMasGrande);

		// TIPOS DE DATOS CON VALORES DECIMILES
		double precioDeLaCarne;
		precioDeLaCarne = 23.2;
		System.out.println("El precio de la carne es: " + precioDeLaCarne);

		float decimal = 2.5f; // Necesita la 'f' para indicar que es un float
		System.out.println("El float es: " + decimal);

		// TIPOS DE DATO TIPO CARACTER
		char caracter = '/';
		System.out.println("El char es: " + caracter);

		// TIPO BOOLEANO
		boolean decision = true;
		System.out.println("La decision es: " + decision);

	}

}
